INSERT INTO hotel(id, name, address, city, zip, hotelImageURL, price, hotelId, description) VALUES (nextval('hibernate_sequence'), 'AieHatt Porte Maillot', '1 Place de la porte Maillot', 'Paris', 75009,'http://localhost:8080/assets/hotel_02.png',110.50,'orFlaqWxc', 'The AieHatt Paris is located in the heart of Paris, just 90 m from the Champs Elysées and 650 m from the Arc de Triomphe. It offers elegantly decorated accommodation, a French restaurant and a lounge bar. Guest rooms at AieHatt Paris are spacious and have floor-to-ceiling windows, which offer plenty of natural light. They feature a flat-screen TV with 38 national and international channels and 82 satellite channels. A kettle, slippers and bathrobes can be provided upon request. The AieHatt Restaurant serves seasonal French cuisine and guests are invited to relax in the W Lounge Bar. A penthouse terrace with views of the Eiffel Tower is available in summer for lunch, dinner and cocktails.');
